import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { auth } from '../firebase';
import '../Login.css';
const Login = () => {
    const history = useHistory();
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const handleSignIn = (e) => {
        e.preventDefault();
        // Firebase stuff
        // dimplemathew@gmail.com

    }
    const handleRegister = (e) => {
        e.preventDefault();
        auth
            .createUserWithEmailAndPassword(email,password)
            .then(auth => {
                // succeful user creation
                console.log(auth)
                if(auth){
                    history.push('/')
                }

            })
            .catch(error => alert(error.message))

    }
    return ( 
        <div className="login">
            <Link to="/">
                <img className="login__logo" 
                src="https://upload.wikimedia.org/wikipedia/commons/thumb/a/a9/Amazon_logo.svg/1024px-Amazon_logo.svg.png" alt="" />
            </Link>
            <div className="login__container">
                <h1>Sign in</h1>
                <form>
                    <h5>E-mail</h5>
                    <input type="text" value={email} onChange={e=> setEmail(e.target.value)} />
                    <h5>Password</h5>
                    <input type="password" value={password} onChange={e => setPassword(e.target.value)}/>
                    <button className='login__signInButton' onClick={handleSignIn}>Sign In</button>
                </form>
                <p>
                    By signing-in you agree to the AMAZON FAKE CLONE Conditions of Use & Sale. Please
                    see our Privacy Notice, our Cookies Notice and our Interest-Based Ads Notice.
                </p>
                <button className='login__registerButton' onClick={handleRegister}>Create your Amazon Account</button>
            </div>

        </div>
     );
}

export default Login;
